import React, { useState } from 'react';
import GoalList from './components/GoalList';
import GoalInput from './components/GoalInput';
import Modal from './components/Modal';
import './App.css';

function App() {
  const [goals, setGoals] = useState(['Learn React', 'Start a business', 'Travel the world']);
  const [newGoal, setNewGoal] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [currentGoal, setCurrentGoal] = useState('');
  const [currentIndex, setCurrentIndex] = useState(null);

  // Ajouter un nouvel objectif
  const addGoal = () => {
    if (newGoal.trim()) {
      setGoals([...goals, newGoal]);
      setNewGoal('');
    }
  };

  // Supprimer un objectif
  const removeGoal = (index) => {
    const updatedGoals = goals.filter((_, i) => i !== index);
    setGoals(updatedGoals);
  };

  // Ouvrir la modale pour éditer
  const openModal = (index) => {
    setIsEditing(true);
    setCurrentGoal(goals[index]);
    setCurrentIndex(index);
    setIsModalOpen(true);
  };

  // Sauvegarder un objectif modifié
  const saveGoal = () => {
    const updatedGoals = goals.map((goal, index) =>
      index === currentIndex ? currentGoal : goal
    );
    setGoals(updatedGoals);
    closeModal();
  };

  // Fermer la modale
  const closeModal = () => {
    setIsModalOpen(false);
    setIsEditing(false);
    setCurrentGoal('');
    setCurrentIndex(null);
  };

  return (
    <div className="App">
      <h1 className="title">Life Goals Management</h1>
      <GoalInput
        newGoal={newGoal}
        setNewGoal={setNewGoal}
        addGoal={addGoal}
      />
      <GoalList
        goals={goals}
        removeGoal={removeGoal}
        openModal={openModal}
      />
      {isModalOpen && (
        <Modal
          currentGoal={currentGoal}
          setCurrentGoal={setCurrentGoal}
          saveGoal={saveGoal}
          closeModal={closeModal}
        />
      )}
    </div>
  );
}

export default App;
