import React from 'react';

function Modal({ currentGoal, setCurrentGoal, saveGoal, closeModal }) {
  return (
    <div className="modal">
      <div className="modal-content">
        <span onClick={closeModal} className="close-btn">‚ùå</span>
        <h2>Edit Goal</h2>
        <input
          type="text"
          value={currentGoal}
          onChange={(e) => setCurrentGoal(e.target.value)}
        />
        <button onClick={saveGoal}>Save</button>
      </div>
    </div>
  );
}

export default Modal;
